const htmlparser2 = require("htmlparser2");
const tags={
    html:"",
    cfg:{
        ignore: {
            noscript: true,
            script:true,
            form:true,
            input:true,
            label:true,
            button:true,
            style:true,
            footer:true,
            link:true
        },
        untouch:{
            pre:true,
            code:true,
            svg:true
        },
        attr:{
            a:["href"],
            img:["src","alt"]
        },

    },
    include:true,
    untouch:false
};
function isIgnore(name) {
    return name in tags.cfg.ignore;
}
function isUntouch(name) {
   return name in tags.cfg.untouch;
}
function writeOpen(name,attrs) {
    if(!(name in tags.cfg.attr)&&!isUntouch(name)&&!tags.untouch) return;
    tags.html+="<";
    tags.html+=name;
    if(name in tags.cfg.attr){
        let ta=tags.cfg.attr[name];
        ta.forEach(a=>{
            if(a in attrs){
                tags.html+=" "+a+"='"+attrs[a]+"'"
            }
        })
    }else if(tags.untouch){
        if(name!="pre"&&name!="code"){
            for(a in attrs){
                tags.html+=" "+a+"='"+attrs[a]+"'"
            }
        }

    }
    tags.html+=">"
}

function simply(str) {
    tags.html="";
    const parser = new htmlparser2.Parser(
        {
            onopentag(name, attribs) {
                if(isIgnore(name)){
                    tags.include=false
                    return;
                }
                if(isUntouch(name)){
                    tags.untouch=true;
                }
                writeOpen(name,attribs)
            },
            async ontext(text) {
                if(tags.include) {
                   tags.html += text;
                }
            },
            onclosetag(name) {
                if(isIgnore(name)){
                    tags.include=true
                    return;
                }
                if(isUntouch(name)){
                    tags.untouch=false;
                }
                if(tags.untouch||(name in tags.cfg.attr)||name in tags.cfg.untouch){
                    tags.html+="</"+name+">";
                }else{
                    if(!tags.html.endsWith("\n")){
                        tags.html+="\n";
                    }
                }
            }
        },
        { decodeEntities: true }
    );
    parser.write(str);
    parser.end();
    const r=tags.html;
    return r;
}

module.exports={simply};


